	[h: displaytext = displaytext+entry+"<table border = 1 width = 100%><tr>"]
	[h, foreach(denom, entrydata): displaytext = displaytext+"<td width="+cellwidth+">"+denom+"</td>"]
	[h: displaytext = displaytext+"</tr><tr>"]
	[h, foreach(denom, entrydata): displaytext = displaytext+"<td width="+cellwidth+">"+json.get(entrydata, denom)+"</td>"]
	[h: displaytext = displaytext+"</tr></table><br>"]
}]

